@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800&display=swap');

:root {
  --color-primary: #ff9800; /* Default primary color (orange) */
  --transition-direction: translateX;
}

/* Base styles */
html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Poppins', sans-serif;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: background-color 0.3s ease, color 0.3s ease;
}

/* Theme classes */
body.dark {
  --bg-main: #121212;
  --bg-card: #1e1e1e;
  --bg-card-hover: #2a2a2a;
  --text-primary: #ffffff;
  --text-secondary: #b0b0b0;
  --border-color: #2a2a2a;

  background-color: var(--bg-main);
  color: var(--text-primary);
}

body.light {
  --bg-main: #f5f5f5;
  --bg-card: #ffffff;
  --bg-card-hover: #f8f8f8;
  --text-primary: #333333;
  --text-secondary: #666666;
  --border-color: #e0e0e0;

  background-color: var(--bg-main);
  color: var(--text-primary);
}

/* Color schemes */
body.purple {
  --color-primary: #9200ee;
}

body.red {
  --color-primary: #fe0000;
}

body.blueviolet {
  --color-primary: #8a2be2;
}

body.blue {
  --color-primary: #0000ff;
}

body.goldenrod {
  --color-primary: #daa520;
}

body.magenta {
  --color-primary: #ff00ff;
}

body.yellowgreen {
  --color-primary: #9acd32;
}

body.orange {
  --color-primary: #ff9800;
}

body.green {
  --color-primary: #008000;
}

body.cyan {
  --color-primary: #00bcd4;
}

body.teal {
  --color-primary: #008080;
}

body.yellow {
  --color-primary: #ffff00;
}

/* Text color utility classes */
.text-primary {
  color: var(--color-primary);
}

.bg-primary {
  background-color: var(--color-primary);
}

.border-primary {
  border-color: var(--color-primary);
}

.hover\:text-primary:hover {
  color: var(--color-primary);
}

.hover\:bg-primary:hover {
  background-color: var(--color-primary);
}

.hover\:border-primary:hover {
  border-color: var(--color-primary);
}

/* Custom animations */
.animate-spin-slow {
  animation: spin 3s linear infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-blink {
  animation: blink 1s step-end infinite;
}

/* Typing text effect */
.typing-text {
  border-right: 2px solid var(--color-primary);
  white-space: nowrap;
  overflow: hidden;
  animation: typing 3.5s steps(30, end), blink 1s step-end infinite;
}

/* Button styles */
.btn-primary {
  @apply bg-primary text-white font-medium py-3 px-6 rounded-lg shadow-md hover:shadow-lg transform hover:-translate-y-1 transition-all duration-300 flex items-center gap-2;
}

/* Card styles */
.card {
  background-color: var(--bg-card);
  border: 1px solid var(--border-color);
  border-radius: 0.75rem;
  transition: all 0.3s ease;
}

.card:hover {
  background-color: var(--bg-card-hover);
  transform: translateY(-4px);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background-color: rgba(155, 155, 155, 0.5);
  border-radius: 20px;
  transition: all 0.3s;
}

::-webkit-scrollbar-thumb:hover {
  background-color: rgba(155, 155, 155, 0.8);
}

/* Glass effect */
.glass-effect {
  backdrop-filter: blur(12px);
  background-color: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 0.75rem;
}

/* Media queries for responsive design */
@media (max-width: 640px) {
  .btn-primary {
    @apply py-2 px-4 text-sm;
  }
}

/* Page transition animations for different directions */

/* Slide Right (default) */
.slide-right-enter {
  opacity: 0;
  transform: translateX(50px);
}

.slide-right-enter-active {
  opacity: 1;
  transform: translateX(0);
  transition: opacity 500ms, transform 500ms;
}

.slide-right-exit {
  opacity: 1;
  transform: translateX(0);
}

.slide-right-exit-active {
  opacity: 0;
  transform: translateX(-50px);
  transition: opacity 500ms, transform 500ms;
}

/* Slide Left */
.slide-left-enter {
  opacity: 0;
  transform: translateX(-50px);
}

.slide-left-enter-active {
  opacity: 1;
  transform: translateX(0);
  transition: opacity 500ms, transform 500ms;
}

.slide-left-exit {
  opacity: 1;
  transform: translateX(0);
}

.slide-left-exit-active {
  opacity: 0;
  transform: translateX(50px);
  transition: opacity 500ms, transform 500ms;
}

/* Slide Top */
.slide-top-enter {
  opacity: 0;
  transform: translateY(-50px);
}

.slide-top-enter-active {
  opacity: 1;
  transform: translateY(0);
  transition: opacity 500ms, transform 500ms;
}

.slide-top-exit {
  opacity: 1;
  transform: translateY(0);
}

.slide-top-exit-active {
  opacity: 0;
  transform: translateY(50px);
  transition: opacity 500ms, transform 500ms;
}

/* Slide Bottom */
.slide-bottom-enter {
  opacity: 0;
  transform: translateY(50px);
}

.slide-bottom-enter-active {
  opacity: 1;
  transform: translateY(0);
  transition: opacity 500ms, transform 500ms;
}

.slide-bottom-exit {
  opacity: 1;
  transform: translateY(0);
}

.slide-bottom-exit-active {
  opacity: 0;
  transform: translateY(-50px);
  transition: opacity 500ms, transform 500ms;
}

/* Keyframe animations */
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes float {
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
  100% {
    transform: translateY(0px);
  }
}

@keyframes blink {
  from, to {
    opacity: 1;
  }
  50% {
    opacity: 0;
  }
}

@keyframes typing {
  from {
    width: 0;
  }
  to {
    width: 100%;
  }
}
