@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800&display=swap');

:root {
  --color-primary: #ff9800; /* Default primary color (orange) */
  --transition-direction: translateX;
}

/* Color schemes */
body.purple {
  --color-primary: #9200ee;
}

body.red {
  --color-primary: #fe0000;
}

body.blueviolet {
  --color-primary: #8a2be2;
}

body.blue {
  --color-primary: #0000ff;
}

body.goldenrod {
  --color-primary: #daa520;
}

body.magenta {
  --color-primary: #ff00ff;
}

body.yellowgreen {
  --color-primary: #9acd32;
}

body.orange {
  --color-primary: #ff9800;
}

body.green {
  --color-primary: #008000;
}

body.yellow {
  --color-primary: #ffff00;
}

body.cyan {
  --color-primary: #00bcd4;
}

body.teal {
  --color-primary: #008080;
}

/* Base styles */
html {
  scroll-behavior: smooth;
}

body {
  @apply font-sans text-gray-600 dark:text-gray-300;
  overflow-x: hidden;
  font-family: 'Poppins', sans-serif;
}

.light-mode {
  @apply bg-gray-50 text-gray-800;
  transition: background-color 0.5s ease, color 0.5s ease;
}

.dark-mode {
  @apply bg-gray-900 text-gray-200;
  transition: background-color 0.5s ease, color 0.5s ease;
}

/* Common styles */
.section-container {
  @apply w-full min-h-screen overflow-hidden relative py-16 sm:py-20;
}

.content-container {
  @apply max-w-[1280px] mx-auto px-4 sm:px-6 lg:px-8;
}

/* Button styles */
.btn-primary {
  @apply bg-primary px-6 py-3 text-white rounded-lg font-medium 
    shadow-md hover:shadow-lg transform hover:-translate-y-1 
    transition-all duration-300 flex items-center gap-2;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  @apply font-bold leading-tight;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background-color: transparent;
}

::-webkit-scrollbar-thumb {
  @apply bg-gray-300 dark:bg-gray-700;
  border-radius: 25px;
  transition: all 0.3s;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-gray-400 dark:bg-gray-600;
}

::-webkit-scrollbar-corner {
  display: none;
}

/* Utility classes */
.text-primary {
  color: var(--color-primary);
}

.bg-primary {
  background-color: var(--color-primary);
}

.bg-primary\/10 {
  background-color: rgba(var(--color-primary), 0.1);
}

.border-primary {
  border-color: var(--color-primary);
}

.hover\:text-primary:hover {
  color: var(--color-primary);
}

.hover\:bg-primary:hover {
  background-color: var(--color-primary);
}

.hover\:border-primary:hover {
  border-color: var(--color-primary);
}

/* Custom animations */
.animate-spin-slow {
  animation: spin 3s linear infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-blink {
  animation: blink 1s step-end infinite;
}

/* Typing text effect */
.typing-text {
  border-right: 2px solid var(--color-primary);
  white-space: nowrap;
  overflow: hidden;
  animation: typing 3.5s steps(30, end), blink 1s step-end infinite;
}

/* Page transition animations */
.fade-enter {
  opacity: 0;
}

.fade-enter-active {
  opacity: 1;
  transition: opacity 500ms;
}

.fade-exit {
  opacity: 1;
}

.fade-exit-active {
  opacity: 0;
  transition: opacity 500ms;
}

/* Enhanced slide animations for transition directions */
.slide-right-enter {
  transform: translateX(100%);
  opacity: 0;
}

.slide-right-enter-active {
  transform: translateX(0);
  opacity: 1;
  transition: transform 500ms, opacity 500ms;
}

.slide-right-exit {
  transform: translateX(0);
  opacity: 1;
}

.slide-right-exit-active {
  transform: translateX(-100%);
  opacity: 0;
  transition: transform 500ms, opacity 500ms;
}

.slide-left-enter {
  transform: translateX(-100%);
  opacity: 0;
}

.slide-left-enter-active {
  transform: translateX(0);
  opacity: 1;
  transition: transform 500ms, opacity 500ms;
}

.slide-left-exit {
  transform: translateX(0);
  opacity: 1;
}

.slide-left-exit-active {
  transform: translateX(100%);
  opacity: 0;
  transition: transform 500ms, opacity 500ms;
}

.slide-top-enter {
  transform: translateY(-100%);
  opacity: 0;
}

.slide-top-enter-active {
  transform: translateY(0);
  opacity: 1;
  transition: transform 500ms, opacity 500ms;
}

.slide-top-exit {
  transform: translateY(0);
  opacity: 1;
}

.slide-top-exit-active {
  transform: translateY(100%);
  opacity: 0;
  transition: transform 500ms, opacity 500ms;
}

.slide-bottom-enter {
  transform: translateY(100%);
  opacity: 0;
}

.slide-bottom-enter-active {
  transform: translateY(0);
  opacity: 1;
  transition: transform 500ms, opacity 500ms;
}

.slide-bottom-exit {
  transform: translateY(0);
  opacity: 1;
}

.slide-bottom-exit-active {
  transform: translateY(-100%);
  opacity: 0;
  transition: transform 500ms, opacity 500ms;
}

/* Keyframe animations */
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes float {
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
  100% {
    transform: translateY(0px);
  }
}

@keyframes blink {
  from, to {
    opacity: 1;
  }
  50% {
    opacity: 0;
  }
}

@keyframes typing {
  from {
    width: 0;
  }
  to {
    width: 100%;
  }
}

/* Glass effect */
.glass-effect {
  @apply backdrop-blur-md bg-white/30 dark:bg-black/30;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

/* Card styles */
.card {
  @apply bg-white dark:bg-gray-800 rounded-xl shadow-md overflow-hidden transition-all duration-300;
}

.card:hover {
  @apply shadow-lg transform -translate-y-1;
}

/* Modern grid layout */
.auto-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  grid-gap: 1.5rem;
}

/* Media queries for responsive design */
@media (max-width: 640px) {
  .section-container {
    @apply py-10;
  }

  .btn-primary {
    @apply px-4 py-2 text-sm;
  }
}
