import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaCheck } from 'react-icons/fa';
import { useTheme } from '../../context/ThemeContext';

const ColorSwitcher = ({ showSettings }) => {
  const { theme } = useTheme();
  const [activeColor, setActiveColor] = useState('orange');

  const colors = [
    { name: 'purple', color: '#9200ee', label: 'Purple' },
    { name: 'red', color: '#fe0000', label: 'Red' },
    { name: 'blueviolet', color: '#8a2be2', label: 'Blue Violet' },
    { name: 'blue', color: '#0000ff', label: 'Blue' },
    { name: 'goldenrod', color: '#daa520', label: 'Gold' },
    { name: 'magenta', color: '#ff00ff', label: 'Magenta' },
    { name: 'yellowgreen', color: '#9acd32', label: 'Green' },
    { name: 'orange', color: '#ff9800', label: 'Orange' },
    { name: 'teal', color: '#008080', label: 'Teal' },
    { name: 'cyan', color: '#00bcd4', label: 'Cyan' },
  ];

  const handleColorChange = (colorName) => {
    document.body.className = colorName;
    document.documentElement.style.setProperty('--color-primary', getComputedStyle(document.body).getPropertyValue('--color-primary'));
    setActiveColor(colorName);

    // Save color preference to local storage
    localStorage.setItem('preferredColor', colorName);
  };

  // Panel animation variants
  const panelVariants = {
    hidden: { 
      opacity: 0, 
      x: 300,
      transition: { type: 'spring', stiffness: 500, damping: 40 }
    },
    visible: { 
      opacity: 1, 
      x: 0, 
      transition: { type: 'spring', stiffness: 500, damping: 30 }
    },
  };

  // Color button animation variants
  const colorButtonVariants = {
    hover: { 
      scale: 1.15, 
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      transition: { type: 'spring', stiffness: 400, damping: 10 }
    },
    tap: { scale: 0.95 }
  };

  return (
    <motion.div
      variants={panelVariants}
      initial="hidden"
      animate={showSettings ? 'visible' : 'hidden'}
      className={`fixed top-20 right-0 w-72 sm:w-80 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} 
        p-6 shadow-2xl rounded-l-2xl z-40 settings-panel overflow-hidden`}
    >
      {/* Panel Header */}
      <div className="mb-6">
        <h3 className={`text-xl font-bold ${theme === 'dark' ? 'text-white' : 'text-gray-800'}`}>
          Customize Theme
        </h3>
        <p className={`text-sm mt-1 ${theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}`}>
          Choose your preferred color scheme
        </p>
      </div>

      {/* Color Grid */}
      <div className="grid grid-cols-5 gap-4 mb-6">
        {colors.map((color) => (
          <motion.button
            key={color.name}
            onClick={() => handleColorChange(color.name)}
            variants={colorButtonVariants}
            whileHover="hover"
            whileTap="tap"
            className={`w-11 h-11 rounded-full flex items-center justify-center 
              ${activeColor === color.name ? 'ring-2 ring-offset-2' : ''} 
              ${theme === 'dark' ? 'ring-white ring-offset-gray-800' : 'ring-gray-800 ring-offset-white'}`}
            style={{ backgroundColor: color.color }}
            aria-label={`Select ${color.label} theme color`}
            title={color.label}
          >
            {activeColor === color.name && (
              <motion.span
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ type: 'spring', stiffness: 500 }}
              >
                <FaCheck className="text-white text-xs" />
              </motion.span>
            )}
          </motion.button>
        ))}
      </div>

      {/* Color Description */}
      <div className={`text-center py-3 px-4 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
        <p className={`text-sm ${theme === 'dark' ? 'text-gray-300' : 'text-gray-700'}`}>
          Your selected color theme will be applied across all pages of the website.
        </p>
      </div>

      {/* Decorative Elements */}
      <motion.div 
        className="absolute -top-12 -right-12 w-24 h-24 rounded-full bg-primary opacity-10"
        animate={{ 
          scale: [1, 1.2, 1],
          rotate: [0, 90, 0]
        }}
        transition={{ 
          duration: 10, 
          repeat: Infinity,
          repeatType: "reverse"
        }}
      />
      <motion.div 
        className="absolute -bottom-8 -left-8 w-16 h-16 rounded-full bg-primary opacity-10"
        animate={{ 
          scale: [1, 1.3, 1],
          rotate: [0, -90, 0]
        }}
        transition={{ 
          duration: 8, 
          repeat: Infinity,
          repeatType: "reverse"
        }}
      />
    </motion.div>
  );
};

export default ColorSwitcher;
