import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');
  const [isThemeLoaded, setIsThemeLoaded] = useState(false);

  // Initialize theme based on localStorage or system preference
  useEffect(() => {
    // Check for saved theme preference or use system preference
    const savedTheme = localStorage.getItem('theme');

    if (savedTheme) {
      setTheme(savedTheme);
      document.documentElement.classList.toggle('dark', savedTheme === 'dark');
      document.body.classList.add(savedTheme === 'dark' ? 'dark-mode' : 'light-mode');
      document.body.classList.remove(savedTheme === 'dark' ? 'light-mode' : 'dark-mode');
    } else {
      // Use system preference
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setTheme(prefersDark ? 'dark' : 'light');
      document.documentElement.classList.toggle('dark', prefersDark);
      document.body.classList.add(prefersDark ? 'dark-mode' : 'light-mode');
    }

    // Mark theme as loaded
    setIsThemeLoaded(true);
  }, []);

  // Initialize color theme
  useEffect(() => {
    const savedColor = localStorage.getItem('preferredColor');
    if (savedColor) {
      document.body.className = savedColor;
      document.body.classList.add(theme === 'dark' ? 'dark-mode' : 'light-mode');
    }
  }, [theme]);

  // Listen for system theme changes
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');

    const handleChange = () => {
      // Only apply system preference if user hasn't set a preference
      if (!localStorage.getItem('theme')) {
        const newTheme = mediaQuery.matches ? 'dark' : 'light';
        setTheme(newTheme);
        document.documentElement.classList.toggle('dark', mediaQuery.matches);
        document.body.classList.add(newTheme === 'dark' ? 'dark-mode' : 'light-mode');
        document.body.classList.remove(newTheme === 'dark' ? 'light-mode' : 'dark-mode');
      }
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);

    // Update document classes
    document.documentElement.classList.toggle('dark', newTheme === 'dark');
    document.body.classList.add(newTheme === 'dark' ? 'dark-mode' : 'light-mode');
    document.body.classList.remove(newTheme === 'dark' ? 'light-mode' : 'dark-mode');

    // Save preference
    localStorage.setItem('theme', newTheme);
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme, isThemeLoaded }}>
      {children}
    </ThemeContext.Provider>
  );
};
