/* Base styles */
html {
  scroll-behavior: smooth;
}

body {
  @apply font-sans text-gray-600 dark:text-gray-300;
  overflow-x: hidden;
  font-family: 'Poppins', sans-serif;
}

.light-mode {
  @apply bg-gray-50 text-gray-800;
  transition: background-color 0.5s ease, color 0.5s ease;
}

.dark-mode {
  @apply bg-gray-900 text-gray-200;
  transition: background-color 0.5s ease, color 0.5s ease;
}

/* Common styles */
.section-container {
  @apply w-full min-h-screen overflow-hidden relative py-16 sm:py-20;
}

.content-container {
  @apply max-w-[1280px] mx-auto px-4 sm:px-6 lg:px-8;
}

/* Button styles */
.btn-primary {
  @apply bg-primary px-6 py-3 text-white rounded-lg font-medium 
    shadow-md hover:shadow-lg transform hover:-translate-y-1 
    transition-all duration-300 flex items-center gap-2;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  @apply font-bold leading-tight;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background-color: transparent;
}

::-webkit-scrollbar-thumb {
  @apply bg-gray-300 dark:bg-gray-700;
  border-radius: 25px;
  transition: all 0.3s;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-gray-400 dark:bg-gray-600;
}

::-webkit-scrollbar-corner {
  display: none;
}

/* Utility classes */
.text-primary {
  color: var(--color-primary);
}

.bg-primary {
  background-color: var(--color-primary);
}

.bg-primary\/10 {
  background-color: rgba(var(--color-primary), 0.1);
}

.border-primary {
  border-color: var(--color-primary);
}

.hover\:text-primary:hover {
  color: var(--color-primary);
}

.hover\:bg-primary:hover {
  background-color: var(--color-primary);
}

.hover\:border-primary:hover {
  border-color: var(--color-primary);
}

/* Custom animations */
.animate-spin-slow {
  animation: spin 3s linear infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-blink {
  animation: blink 1s step-end infinite;
}

/* Typing text effect */
.typing-text {
  border-right: 2px solid var(--color-primary);
  white-space: nowrap;
  overflow: hidden;
  animation: typing 3.5s steps(30, end), blink 1s step-end infinite;
}

/* Page transition animations */
.fade-enter {
  opacity: 0;
}

.fade-enter-active {
  opacity: 1;
  transition: opacity 500ms;
}

.fade-exit {
  opacity: 1;
